import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

// ✅ Fetch full user info by email (must return clientId too from backend)
export const fetchUserByEmail = createAsyncThunk(
  'auth/fetchUser',
  async (email, { rejectWithValue }) => {
    try {
      const response = await axios.get(`http://localhost:8080/api/user/${email}`);
      return response.data; // Must contain: user_id, email, fullName, role, (optionally clientId)
    } catch (error) {
      return rejectWithValue(error.response?.data || "User fetch failed");
    }
  }
);

// ✅ Optional: register user
export const registerUser = createAsyncThunk(
  'auth/registerUser',
  async (userData, { rejectWithValue }) => {
    try {
      const response = await axios.post("http://localhost:8080/api/register", userData);
      return response.data;
    } catch (error) {
      return rejectWithValue(error.response?.data || "Registration failed");
    }
  }
);

const authSlice = createSlice({
  name: 'auth',
  initialState: {
    isLoggedIn: false,
    user: null,
    loading: false,
    error: null,
    message: null,
  },
  reducers: {
    loginSuccess: (state, action) => {
      state.isLoggedIn = true;
      state.user = action.payload;
      state.error = null;
    },
    logout: (state) => {
      state.isLoggedIn = false;
      state.user = null;
      state.message = null;
      state.error = null;
    },
  },
  extraReducers: (builder) => {
    builder
      // Fetch user details after login
      .addCase(fetchUserByEmail.pending, (state) => {
        state.loading = true;
        state.error = null;
      })
      .addCase(fetchUserByEmail.fulfilled, (state, action) => {
        state.loading = false;

        const userData = action.payload;

        // ✅ Ensure clientId is included if role is CLIENT
        state.user = {
          user_id: userData.user_id,
          email: userData.email,
          fullName: userData.fullName,
          contact: userData.contact,
          role: userData.role,
          clientId: userData.clientId || null, // <-- Add this safely
        };

        state.isLoggedIn = true;
      })
      .addCase(fetchUserByEmail.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      })

      // Register user
      .addCase(registerUser.pending, (state) => {
        state.loading = true;
        state.error = null;
      })
      .addCase(registerUser.fulfilled, (state, action) => {
        state.loading = false;
        state.message = action.payload;
      })
      .addCase(registerUser.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      });
  }
});

export const { loginSuccess, logout } = authSlice.actions;
export default authSlice.reducer;
