import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    role: 'Client'
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Logged in as ${formData.role}`);
    // later: connect to backend
  };

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="card shadow">
            <div className="card-body">
              <h3 className="text-center mb-4">Login</h3>
              <form onSubmit={handleSubmit}>
                <input className="form-control mb-3" type="email" name="email" placeholder="Email" required onChange={handleChange} />
                <input className="form-control mb-3" type="password" name="password" placeholder="Password" required onChange={handleChange} />
                <select className="form-select mb-3" name="role" onChange={handleChange}>
                  <option value="Client">Client</option>
                  <option value="Vendor">Vendor</option>
                  <option value="Admin">Admin</option>
                </select>
                <button className="btn btn-primary w-100" type="submit">Login</button>
              </form>
              <p className="text-center mt-3">New here? <a href="/register">Create an account</a></p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
