import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    const loginData = {
      email: email,
      password: password
    };

    try {
      const response = await axios.post('http://localhost:8080/api/login', loginData);

       console.log("User Data:", response.data);
      alert(`Login successful as ${response.data.user_name} (${response.data.role.rname})`);

      // Save user info to localStorage for session (optional)
      localStorage.setItem('user', JSON.stringify(user));

      // Redirect based on role
      if (user.role.rname === 'Client') {
        navigate('/client-dashboard');
      } else if (user.role.rname === 'Vendor') {
        navigate('/vendor-dashboard');
      } else {
        navigate('/');
      }

    } catch (error) {
      console.error(error);
      alert('Invalid email or password');
    }
  };

  return (
    <div className="container mt-5">
      <h2>Login</h2>
      <form onSubmit={handleLogin} className="col-md-6 mx-auto">
        <div className="form-group mb-3">
          <label>Email</label>
          <input
            type="email"
            className="form-control"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>

        <div className="form-group mb-4">
          <label>Password</label>
          <input
            type="password"
            className="form-control"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>

        <button type="submit" className="btn btn-primary w-100">Login</button>
      </form>
    </div>
  );
}

export default Login;
