import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    const userData = {
      email,
      user_password: password,
    };

    try {
      const response = await axios.post('http://localhost:8080/api/login', userData);

      // ✅ Log the response to see what you're getting from backend
      console.log("User Data:", response.data);

      // ✅ Use response.data safely
      const loggedInUser = response.data;

      alert(`Login successful as ${loggedInUser.user_name} (${loggedInUser.role.rname})`);

      if (loggedInUser.role.rname === 'Client') {
        navigate('/client-dashboard');
      } else if (loggedInUser.role.rname === 'Vendor') {
        navigate('/vendor-dashboard');
      }
    } catch (error) {
      console.error("Login failed:", error);
      alert(error.response?.data || "Login failed");
    }
  };

  return (
    <div className="container mt-5">
      <h2>Login</h2>
      <form onSubmit={handleSubmit} className="col-md-6 mx-auto">
        <div className="form-group mb-3">
          <label>Email</label>
          <input
            type="email"
            className="form-control"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>

        <div className="form-group mb-3">
          <label>Password</label>
          <input
            type="password"
            className="form-control"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>

        <button type="submit" className="btn btn-primary w-100">Login</button>
      </form>
    </div>
  );
}

export default Login;
