import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axiosAuth from '../API/axiosAuth';

export const fetchUserByEmail = createAsyncThunk(
  'auth/fetchUser',
  async (email, { rejectWithValue }) => {
    try {
      const response = await axiosAuth.get(`/api/user/${email}`);
      return response.data;
    } catch (error) {
      return rejectWithValue(error.response?.data || "User fetch failed");
    }
  }
);

export const registerUser = createAsyncThunk(
  'auth/registerUser',
  async (userData, { rejectWithValue }) => {
    try {
      const response = await axiosAuth.post("/api/register", userData);
      return response.data;
    } catch (error) {
      return rejectWithValue(error.response?.data || "Registration failed");
    }
  }
);

const authSlice = createSlice({
  name: 'auth',
  initialState: {
    isLoggedIn: false,
    user: null,
    loading: false,
    error: null,
    message: null,
  },
  reducers: {
    loginSuccess: (state, action) => {
      state.isLoggedIn = true;
      state.user = action.payload;
      state.error = null;
    },
    logout: (state) => {
      state.isLoggedIn = false;
      state.user = null;
      state.message = null;
      state.error = null;
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(fetchUserByEmail.pending, (state) => {
        state.loading = true;
      })
      .addCase(fetchUserByEmail.fulfilled, (state, action) => {
        state.loading = false;
        state.user = {
          user_id: action.payload.user_id,
          email: action.payload.email,
          fullName: action.payload.fullName,
          contact: action.payload.contact,
          role: action.payload.role,
          clientId: action.payload.clientId || null,
        };
        state.isLoggedIn = true;
      })
      .addCase(fetchUserByEmail.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      })
      .addCase(registerUser.pending, (state) => {
        state.loading = true;
      })
      .addCase(registerUser.fulfilled, (state, action) => {
        state.loading = false;
        state.message = action.payload;
      })
      .addCase(registerUser.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      });
  },
});

export const { loginSuccess, logout } = authSlice.actions;
export default authSlice.reducer;
