import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axiosClient from "../API/axiosClient";

// ðŸ”„ Fetch all requirements for a client
export const fetchRequirementsByEmail = createAsyncThunk(
  "requirements/fetchByEmail",
  async (email, { rejectWithValue }) => {
    try {
      const response = await axiosClient.get(`/client/requirements/${email}`);
      return response.data;
    } catch (err) {
      return rejectWithValue(err.response?.data || "Failed to fetch requirements");
    }
  }
);

// âœ… POST a new requirement
export const createRequirement = createAsyncThunk(
  "requirements/createRequirement",
  async (requirementData, { getState, rejectWithValue }) => {
    try {
      const state = getState();
      const clientId = state.auth.clientId;

      if (!clientId) {
        throw new Error("Missing clientId from Redux");
      }

      const payload = {
        title: requirementData.title,
        description: requirementData.description,
        budget: parseFloat(requirementData.budget),
        client: {
          clientId: clientId,
        },
      };

      const response = await axiosClient.post("/requirements", payload);
      return response.data;
    } catch (err) {
      return rejectWithValue(err.response?.data || err.message || "Requirement creation failed");
    }
  }
);

const requirementsSlice = createSlice({
  name: "requirements",
  initialState: {
    requirements: [],
    loading: false,
    error: null,
  },
  reducers: {},
  extraReducers: (builder) => {
    builder
      // fetch requirements
      .addCase(fetchRequirementsByEmail.pending, (state) => {
        state.loading = true;
      })
      .addCase(fetchRequirementsByEmail.fulfilled, (state, action) => {
        state.loading = false;
        state.requirements = action.payload;
      })
      .addCase(fetchRequirementsByEmail.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      })

      // post requirement
      .addCase(createRequirement.pending, (state) => {
        state.loading = true;
      })
      .addCase(createRequirement.fulfilled, (state, action) => {
        state.loading = false;
        state.requirements.push(action.payload); // âœ… auto-add
      })
      .addCase(createRequirement.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      });
  },
});

export default requirementsSlice.reducer;
