import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useDispatch } from 'react-redux';
import { loginSuccess } from '../redux/authSlice'; // ‚úÖ updated import

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // ‚úÖ Step 1: Login request
      const loginResponse = await axios.post('http://localhost:8080/api/login', {
        email,
        password,
      });

      // ‚úÖ Step 2: Get full user details
      const userResponse = await axios.get(`http://localhost:8080/api/user/${email}`);
      const user = userResponse.data;

      // ‚úÖ Step 3: Store in Redux
      dispatch(loginSuccess(user));

      // ‚úÖ Step 4: Role-based routing
      if (user.role === 'Client') {
        navigate('/client-dashboard');
      } else if (user.role === 'Vendor') {
        navigate('/vendor-dashboard');
      } else if (user.role === 'Admin') {
        window.location.href = 'http://localhost:your-dotnet-admin-url'; // üîÅ Replace with actual admin path
      }

    } catch (error) {
      console.error(error);
      alert('Login failed. Please check your credentials.');
    }
  };

  return (
    <div className="container mt-5">
      <h2 className="mb-4 text-center">Login</h2>
      <form onSubmit={handleSubmit} className="col-md-6 mx-auto shadow p-4 rounded bg-light">
        <div className="form-group mb-3">
          <label>Email</label>
          <input
            type="email"
            className="form-control"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            placeholder="Enter your email"
          />
        </div>

        <div className="form-group mb-4">
          <label>Password</label>
          <input
            type="password"
            className="form-control"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            placeholder="Enter your password"
          />
        </div>

        <button type="submit" className="btn btn-success w-100">Login</button>
      </form>
    </div>
  );
}

export default Login;
