import React, { useState } from 'react';
import './Register.css';

const Register = () => {
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    password: '',
    contact: '',
    role: 'Client'
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Registered as ${formData.role}\nName: ${formData.fullName}\nEmail: ${formData.email}`);
    // Later: send formData to backend API
  };

  return (
    <div className="register-container">
      <h2>Create Your MeetYourNeed Account</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="fullName" placeholder="Full Name" required onChange={handleChange} />
        <input type="email" name="email" placeholder="Email Address" required onChange={handleChange} />
        <input type="password" name="password" placeholder="Password" required onChange={handleChange} />
        <input type="text" name="contact" placeholder="Contact Number" required onChange={handleChange} />
        
        <select name="role" onChange={handleChange}>
          <option value="Client">Client</option>
          <option value="Vendor">Vendor</option>
        </select>
        
        <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default Register;
