import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axiosAuth from '../API/axiosAuth';

// ðŸ”„ Fetch user by email (used after login)
export const fetchUserByEmail = createAsyncThunk(
  'auth/fetchUser',
  async (email, { rejectWithValue }) => {
    try {
      const response = await axiosAuth.get(`/user/${email}`);
      console.log("âœ… fetchUserByEmail response:", response.data);
      return response.data;
    } catch (error) {
      console.error("âŒ fetchUserByEmail failed:", error);
      return rejectWithValue(error.response?.data || "User fetch failed");
    }
  }
);

// ðŸ“ Register user
export const registerUser = createAsyncThunk(
  'auth/registerUser',
  async (userData, { rejectWithValue }) => {
    try {
      const response = await axiosAuth.post("/register", userData);
      return response.data;
    } catch (error) {
      return rejectWithValue(error.response?.data || "Registration failed");
    }
  }
);

const authSlice = createSlice({
  name: 'auth',
  initialState: {
    isLoggedIn: false,
    user: null,
    clientId: null,
    loading: false,
    error: null,
    message: null,
  },
  reducers: {
    loginSuccess: (state, action) => {
      const data = action.payload;
      state.isLoggedIn = true;
      state.user = {
        userId: data.userId,
        email: data.email,
        fullName: data.user_name || data.fullName || "", // âœ… normalize
        contact: data.contact,
        role: data.role,
      };
      state.clientId = data.clientId || null;
      state.error = null;
    },
    logout: (state) => {
      state.isLoggedIn = false;
      state.user = null;
      state.clientId = null;
      state.message = null;
      state.error = null;
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(fetchUserByEmail.pending, (state) => {
        state.loading = true;
        state.error = null;
      })
      .addCase(fetchUserByEmail.fulfilled, (state, action) => {
        const data = action.payload;
        state.loading = false;
        state.clientId = data.clientId || null;
        state.user = {
          userId: data.userId,
          email: data.email,
          fullName: data.user_name || data.fullName || "", // âœ… normalize
          contact: data.contact,
          role: data.role,
        };
        state.isLoggedIn = true;
      })
      .addCase(fetchUserByEmail.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      })
      .addCase(registerUser.pending, (state) => {
        state.loading = true;
        state.error = null;
      })
      .addCase(registerUser.fulfilled, (state, action) => {
        state.loading = false;
        state.message = action.payload;
      })
      .addCase(registerUser.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      });
  },
});

export const { loginSuccess, logout } = authSlice.actions;
export default authSlice.reducer;
