import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { createRequirement } from "../features/requirements/requirementsSlice";

function AddRequirementForm({ onRequirementAdded }) {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [budget, setBudget] = useState("");

  const dispatch = useDispatch();

  // ‚úÖ Get clientId from Redux auth slice
  const clientId = useSelector((state) => state.auth.user?.clientId);

  const handleSubmit = async (e) => {
    e.preventDefault();

    // ‚úÖ Prevent submission if clientId is missing
    if (!clientId) {
      alert("üö´ Client ID missing. Please log in again.");
      return;
    }

    const newRequirement = {
      title,
      description,
      budget,
      client: {
        clientId: clientId, // ‚úÖ foreign key
      },
    };

    try {
      // ‚úÖ Dispatch to Redux to hit backend API
      await dispatch(createRequirement(newRequirement)).unwrap();
      alert("‚úÖ Requirement added successfully!");
      
      // ‚úÖ Reset form
      setTitle("");
      setDescription("");
      setBudget("");

      // ‚úÖ Optionally inform parent
      if (onRequirementAdded) {
        onRequirementAdded();
      }
    } catch (error) {
      console.error("‚ùå Failed to add requirement:", error);
      alert("‚ùå Failed to add requirement. Please try again.");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h3 className="text-xl font-bold mb-4">‚ûï Add New Requirement</h3>

      <div className="mb-4">
        <label className="block text-sm font-medium">Title:</label>
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className="w-full border p-2 rounded"
          required
        />
      </div>

      <div className="mb-4">
        <label className="block text-sm font-medium">Description:</label>
        <textarea
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          className="w-full border p-2 rounded"
          required
        />
      </div>

      <div className="mb-4">
        <label className="block text-sm font-medium">Budget (INR):</label>
        <input
          type="number"
          value={budget}
          onChange={(e) => setBudget(e.target.value)}
          className="w-full border p-2 rounded"
          required
        />
      </div>

      <button
        type="submit"
        className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
      >
        Submit Requirement
      </button>
    </form>
  );
}

export default AddRequirementForm;
