import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { createRequirement } from "../redux/requirementsSlice";

function AddRequirementForm({ onRequirementAdded }) {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [budget, setBudget] = useState("");

  const dispatch = useDispatch();

  // ✅ Get clientId from Redux state
  const clientId = useSelector((state) => state.auth.user?.clientId);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!clientId) {
      alert("🚫 Client ID missing. Please log in again.");
      return;
    }

    const newRequirement = {
      title,
      description,
      budget,
      client: {
        clientId,
      },
    };

    try {
      await dispatch(createRequirement(newRequirement)).unwrap();
      alert("✅ Requirement added successfully!");

      setTitle("");
      setDescription("");
      setBudget("");

      if (onRequirementAdded) {
        onRequirementAdded();
      }
    } catch (error) {
      console.error("❌ Failed to add requirement:", error);
      alert("❌ Failed to add requirement. Please try again.");
    }
  };

  return (
    <div className="max-w-xl mx-auto mt-6 bg-white rounded-2xl shadow-lg p-6 border border-gray-200">
      <h2 className="text-2xl font-semibold text-gray-800 mb-6 text-center">
        ➕ Add New Requirement
      </h2>

      <form onSubmit={handleSubmit} className="space-y-5">
        <div>
          <label className="block text-gray-700 font-medium mb-1">Title</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="e.g. Build a portfolio website"
            className="w-full px-4 py-2 border border-gray-300 rounded-xl shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-400"
            required
          />
        </div>

        <div>
          <label className="block text-gray-700 font-medium mb-1">Description</label>
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Describe your requirement..."
            rows="4"
            className="w-full px-4 py-2 border border-gray-300 rounded-xl shadow-sm resize-none focus:outline-none focus:ring-2 focus:ring-blue-400"
            required
          />
        </div>

        <div>
          <label className="block text-gray-700 font-medium mb-1">Budget (₹)</label>
          <input
            type="number"
            value={budget}
            onChange={(e) => setBudget(e.target.value)}
            placeholder="e.g. 10000"
            className="w-full px-4 py-2 border border-gray-300 rounded-xl shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-400"
            required
          />
        </div>

        <button
          type="submit"
          className="w-full bg-blue-600 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded-xl shadow-md transition duration-200"
        >
          🚀 Submit Requirement
        </button>
      </form>
    </div>
  );
}

export default AddRequirementForm;
