import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // üîê In actual backend logic:
    // - Validate credentials
    // - Detect role from database (Client/Vendor/Admin)
    // - Redirect based on that

    alert(`Logged in with email: ${formData.email}`);
    // Example redirect: navigate('/client-dashboard') or /vendor-dashboard or /admin-dashboard
  };

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="card shadow-sm">
            <div className="card-body">
              <h3 className="text-center mb-4 text-dark">Login</h3>
              <form onSubmit={handleSubmit}>
                <input className="form-control mb-3" type="email" name="email" placeholder="Email" required onChange={handleChange} />
                <input className="form-control mb-3" type="password" name="password" placeholder="Password" required onChange={handleChange} />

                <button className="btn w-100" style={{ backgroundColor: '#1abc9c', color: 'white' }} type="submit">
                  Login
                </button>
              </form>
              <p className="text-center mt-3">New here? <a href="/register">Create an account</a></p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
