import React, { useState } from 'react';
import axios from 'axios';

const Register = () => {
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    password: '',
    contact: '',
    role: 'Client'
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:8080/api/register', formData);
      alert(res.data);
    } catch (error) {
      alert(error.response?.data || "Registration failed");
    }
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center">Register</h2>
      <form onSubmit={handleSubmit} className="w-50 mx-auto">
        <input className="form-control mb-2" type="text" name="fullName" placeholder="Full Name" onChange={handleChange} required />
        <input className="form-control mb-2" type="email" name="email" placeholder="Email" onChange={handleChange} required />
        <input className="form-control mb-2" type="password" name="password" placeholder="Password" onChange={handleChange} required />
        <input className="form-control mb-2" type="text" name="contact" placeholder="Contact" onChange={handleChange} required />
        <select className="form-select mb-2" name="role" onChange={handleChange}>
          <option value="Client">Client</option>
          <option value="Vendor">Vendor</option>
        </select>
        <button className="btn btn-success w-100">Register</button>
      </form>
    </div>
  );
};

export default Register;
