import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Register = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    password: '',
    contact: '',
    role: 'Client'
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Registered as ${formData.role}`);
    navigate('/login');
  };

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="card shadow">
            <div className="card-body">
              <h3 className="text-center mb-4">Register</h3>
              <form onSubmit={handleSubmit}>
                <input className="form-control mb-3" type="text" name="fullName" placeholder="Full Name" required onChange={handleChange} />
                <input className="form-control mb-3" type="email" name="email" placeholder="Email" required onChange={handleChange} />
                <input className="form-control mb-3" type="password" name="password" placeholder="Password" required onChange={handleChange} />
                <input className="form-control mb-3" type="text" name="contact" placeholder="Contact Number" required onChange={handleChange} />

                <select className="form-select mb-3" name="role" onChange={handleChange}>
                  <option value="Client">Client</option>
                  <option value="Vendor">Vendor</option>
                </select>

                <button className="btn btn-primary w-100" type="submit">Register</button>
              </form>
              <p className="text-center mt-3">Already have an account? <a href="/login">Login here</a></p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Register;
