import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Register.css';

const Register = () => {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    password: '',
    contact: '',
    role: 'Client'
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Registered as ${formData.role}\nName: ${formData.fullName}`);
    navigate('/login');
  };

  return (
    <div className="register-page">
      <div className="register-box">
        <h2>Register</h2>
        <form onSubmit={handleSubmit}>
          <input type="text" name="fullName" placeholder="Full Name" required onChange={handleChange} />
          <input type="email" name="email" placeholder="Email Address" required onChange={handleChange} />
          <input type="password" name="password" placeholder="Password" required onChange={handleChange} />
          <input type="text" name="contact" placeholder="Contact Number" required onChange={handleChange} />

          <select name="role" onChange={handleChange}>
            <option value="Client">Client</option>
            <option value="Vendor">Vendor</option>
          </select>

          <button type="submit">Register</button>
        </form>
        <p className="link">Already have an account? <a href="/login">Login here</a></p>
      </div>
    </div>
  );
};

export default Register;
