import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Login = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:8080/api/login', formData);
      const message = res.data;

      alert(message); // Shows: "Login successful as Client" (or Vendor/Admin)

      // Redirect user based on role
      if (message.includes("Client")) {
        navigate("/client-dashboard");
      } else if (message.includes("Vendor")) {
        navigate("/vendor-dashboard");
      } else if (message.includes("Admin")) {
        navigate("/admin-dashboard");
      }
    } catch (error) {
      alert(error.response?.data || "Login failed");
    }
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center">Login</h2>
      <form onSubmit={handleSubmit} className="w-50 mx-auto">
        <input
          className="form-control mb-3"
          type="email"
          name="email"
          placeholder="Email"
          required
          onChange={handleChange}
        />
        <input
          className="form-control mb-3"
          type="password"
          name="password"
          placeholder="Password"
          required
          onChange={handleChange}
        />
        <button className="btn btn-primary w-100" type="submit">
          Login
        </button>
      </form>
    </div>
  );
};

export default Login;
